// ===============================
// auth.js ‚Äì Registration & Login with Role Redirect (Admin-Protected)
// ===============================

import { initializeApp } from "https://www.gstatic.com/firebasejs/12.4.0/firebase-app.js";
import {
  getAuth,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "https://www.gstatic.com/firebasejs/12.4.0/firebase-auth.js";
import {
  getFirestore,
  doc,
  setDoc,
  getDoc
} from "https://www.gstatic.com/firebasejs/12.4.0/firebase-firestore.js";

// üîπ Firebase Config
const firebaseConfig = {
  apiKey: "AIzaSyA5milUoYfNSMhif0JA7dbw26tyfs2fDpQ",
  authDomain: "campushire-d0dd8.firebaseapp.com",
  projectId: "campushire-d0dd8",
  storageBucket: "campushire-d0dd8.appspot.com",
  messagingSenderId: "448246002591",
  appId: "1:448246002591:web:098670b92661a0a3a283aa",
  measurementId: "G-TFJV87Q6QP",
};

const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);

// ===============================
// REGISTER
// ===============================
const registerForm = document.getElementById("registerForm");

if (registerForm) {
  registerForm.addEventListener("submit", async (e) => {
    e.preventDefault();
    const name = document.getElementById("name").value.trim();
    const email = document.getElementById("email").value.trim();
    const password = document.getElementById("password").value.trim();
    const role = document.getElementById("role").value;

    if (!role) return alert("Please select a role.");
    if (role === "admin") {
      alert("Error: You cannot register as an admin.");
      return;
    }

    try {
      const { user } = await createUserWithEmailAndPassword(auth, email, password);

      await setDoc(doc(db, "users", user.uid), {
        name,
        email,
        role,
        companyName: "",
        about: "",
        skills: "",
        createdAt: new Date().toISOString()
      });

      alert("Success: Registration successful!");
      window.location.href = "login.html";
    } catch (err) {
      alert("Error: " + err.message);
    }
  });
}

// ===============================
// LOGIN
// ===============================
const loginForm = document.getElementById("loginForm");

if (loginForm) {
  loginForm.addEventListener("submit", async (e) => {
    e.preventDefault();
    const email = document.getElementById("loginEmail").value.trim();
    const password = document.getElementById("loginPassword").value.trim();

    try {
      const { user } = await signInWithEmailAndPassword(auth, email, password);
      const userSnap = await getDoc(doc(db, "users", user.uid));

      if (!userSnap.exists()) {
        alert("Error: Account not found. Please contact admin.");
        return;
      }

      const data = userSnap.data();
      const role = data.role;

      switch (role) {
        case "student":
          window.location.href = "student_dashboard.html";
          break;
        case "recruiter":
          window.location.href = "recruiter_dashboard.html";
          break;
        case "admin":
          window.location.href = "admin_dashboard.html";
          break;
        default:
            alert("Error: Unknown or unauthorized role.");
      }
    } catch (err) {
        alert("Error: " + err.message);
    }
  });
}

css/dashboard.css.content:
/* ==============================
   DASHBOARD PAGES
   Student, Recruiter, and Admin dashboards
   ============================== */

.dashboard-container {
  max-width: 1100px;
  margin: 28px auto;
  background: var(--card);
  padding: 28px;
  border-radius: var(--radius);
  box-shadow: var(--shadow-md);
  animation: fadeIn 0.5s ease-out;
}

.dashboard-container h1 {
  margin-top: 0;
  margin-bottom: 20px;
  color: var(--primary-600);
}

/* Dashboard Sections */
.dashboard-section {
  background: var(--card);
  border-radius: 10px;
  padding: 18px;
  margin-bottom: 20px;
  box-shadow: var(--shadow-sm);
  transition: all 0.2s ease;
}

.dashboard-section:hover {
  box-shadow: var(--shadow-md);
}

.dashboard-section h3 {
  margin-top: 0;
  margin-bottom: 12px;
  color: #0f172a;
  font-size: 1.1rem;
}

/* Stats Grid */
.stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  gap: 12px;
  margin: 18px 0;
}

.card {
  background: var(--card);
  padding: 16px;
  border-radius: 10px;
  box-shadow: var(--shadow-sm);
  transition: transform 0.18s ease, box-shadow 0.18s ease;
  text-align: center;
}

.card:hover {
  transform: translateY(-6px);
  box-shadow: var(--shadow-md);
}

.card b {
  display: block;
  font-size: 1.8rem;
  color: var(--primary-600);
  margin-top: 8px;
}

.card small {
  display: block;
  color: var(--muted);
  margin-top: 4px;
}

/* Job/User Cards */
.job-item {
  background: #fff;
  border-radius: 10px;
  padding: 12px;
  border: 1px solid #eef3fb;
  box-shadow: var(--shadow-sm);
  transition: transform 0.14s ease;
  margin-bottom: 12px;
}

.job-item:hover {
  transform: translateY(-4px);
  box-shadow: var(--shadow-md);
}

.job-item b {
  font-size: 1rem;
  color: #0f172a;
  display: block;
  margin-bottom: 6px;
}

.job-item small {
  display: block;
  color: var(--muted);
  margin-top: 6px;
}

/* Applicant Cards */
.applicant-card {
  display: flex;
  gap: 12px;
  align-items: center;
  background: #fff;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #eef3fb;
  margin-bottom: 12px;
  transition: all 0.2s ease;
}

.applicant-card:hover {
  box-shadow: var(--shadow-sm);
  transform: translateX(4px);
}

.applicant-card img {
  border-radius: 50%;
  width: 56px;
  height: 56px;
  object-fit: cover;
  border: 2px solid #eef6ff;
}

/* User Info */
.user-info {
  background: #fff;
  padding: 12px;
  border-radius: 8px;
  border: 1px solid #eef3fb;
}

/* Job Form */
.job-form {
  display: block;
}

.job-form input,
.job-form textarea,
.job-form select {
  margin-bottom: 12px;
}

.job-form button {
  width: 100%;
  margin-top: 4px;
}

/* Button Groups */
.btn-group {
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
  justify-content: center;
  margin-top: 16px;
}

.btn-group button {
  flex: 1;
  min-width: 140px;
}

/* Search/Filter */
.search-filter {
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
  flex-wrap: wrap;
}

.search-filter input,
.search-filter select {
  flex: 1;
  min-width: 200px;
}

.search-filter button {
  flex: 0 0 auto;
  min-width: 100px;
}

/* Status Badges */
.status-badge {
  display: inline-block;
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 0.85rem;
  font-weight: 600;
}

.status-active {
  background: #d4edda;
  color: #155724;
}

.status-closed {
  background: #f8d7da;
  color: #721c24;
}

/* Responsive */
@media (max-width: 768px) {
  .dashboard-container {
    padding: 16px;
    margin: 12px;
    border-radius: 8px;
  }

  .dashboard-container h1 {
    font-size: 1.5rem;
    margin-bottom: 16px;
  }

  .row {
    flex-direction: column;
  }

  button, input[type=submit] {
    width: 100%;
  }

  .stats {
    grid-template-columns: 1fr;
  }

  .btn-group {
    flex-direction: column;
  }

  .btn-group button {
    width: 100%;
  }

  .search-filter {
    flex-direction: column;
  }

  .search-filter input,
  .search-filter select,
  .search-filter button {
    width: 100%;
  }

  .job-item {
    padding: 10px;
    margin-bottom: 10px;
  }

  .applicant-card {
    flex-direction: column;
    align-items: flex-start;
  }

  .user-card-compact,
  .job-card-compact {
    flex-direction: column;
    align-items: flex-start !important;
  }

  .user-card-compact > div:last-child,
  .job-card-compact > div:last-child {
    width: 100%;
    justify-content: space-between;
  }
}

@media (max-width: 480px) {
  .dashboard-container {
    padding: 12px;
    margin: 8px;
  }

  .dashboard-container h1 {
    font-size: 1.3rem;
  }

  .card {
    padding: 12px;
  }

  .card b {
    font-size: 1.5rem;
  }

  .stats {
    gap: 8px;
  }

  .dashboard-section {
    padding: 12px;
    margin-bottom: 12px;
  }

  .dashboard-section h3 {
    font-size: 1rem;
    margin-bottom: 10px;
  }

  input, textarea, select {
    font-size: 16px !important;
  }
}

